create table mercado (id bigint generated by default as identity, cnpj varchar(255) not null, expediente_inicio timestamp(6) not null, expediente_termino timestamp(6) not null, nome varchar(255) not null, preco_minimo float(53) not null, telefone varchar(255) not null, primary key (id));
create table produto (id bigint generated by default as identity, fabricante varchar(255) not null, frete float(53) not null, nome varchar(255) not null, peso float(53) not null, preco float(53) not null, quantidade integer not null, vencimento timestamp(6), volume float(53) not null, mercado_id bigint, primary key (id));
create table usuario (id bigint generated by default as identity, email varchar(255), nome varchar(255), senha varchar(255), primary key (id));
alter table if exists produto add constraint FKmercado_produto foreign key (mercado_id) references mercado;
